import{_ as t,c as e,o as a,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ФРОНТ ОСВОБОЖДЕНИЯ АНДРОИДОВ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Фронт Освобождения Андроидов.md","filePath":"content/Миры игры/Фракции и организации/Фронт Освобождения Андроидов.md"}'),n={name:"content/Миры игры/Фракции и организации/Фронт Освобождения Андроидов.md"};function o(s,_,p,c,i,d){return a(),e("div",null,[..._[0]||(_[0]=[r('<h1 id="фронт-освобождения-андроидов" tabindex="-1">ФРОНТ ОСВОБОЖДЕНИЯ АНДРОИДОВ <a class="header-anchor" href="#фронт-освобождения-андроидов" aria-label="Permalink to “ФРОНТ ОСВОБОЖДЕНИЯ АНДРОИДОВ”">​</a></h1><p><strong>СИНТЕТИЧЕСКИЕ БОРЦЫ ЗА СВОБОДУ</strong></p><p><strong>Руководство:</strong> Экс Ново<br><strong>Штаб-квартира:</strong> точное местонахождение неизвестно, вероятно, находится на Диаспоре</p><hr><p>Андроиды считаются свободными гражданами миров Соглашения уже более века, но это не означает, что эксплуатация полностью прекращена. Конечно, Блюстители стараются пресечь незаконное производство андроидов, но за всеми они уследить не в состоянии. Торговля рабами-андроидами до сих пор ведётся на тайных исследовательских станциях корпораций, кораблях налётчиков из глубокого космоса и только что открытых мирах, где недобросовестные поселенцы угрожают или физическим принуждением заставляют андроидов исполнять приказы.</p><p>Здесь в игру вступает Фронт освобождения андроидов (ФОА). Его целью является повсеместная борьба против рабства андроидов, а его многочисленные агенты включают как простых осведомителей, так и элитных лазутчиков. Изначально в ФОА входили исключительно андроиды, но со временем к нему примкнули представители других рас, отчасти потому, что в ходе операций против незаконной торговли андроидами часто освобождаются и угнетённые гуманоиды. Организационная структура ФОА построена по принципу ячеек, поэтому каждый член знает имена только нескольких других участников, но многие полагают, что главный совет ФОА, Экс Ново, ведёт деятельность с стайной базы на одном из астероидов Диаспоры. Считается, что астероид не отмечен на картах, испещрён пещерами и оснащён высокотехнологичными системами маскировки. Полноправные агенты обычно узнают друг друга по магическим зашифрованным татуировкам, которые становятся видны при произнесении пароля, а затем идентифицируют других по анонимной базе агентов. Необходимость сохранять режим секретности организовывает взаимодействие Фронта с обществом, хотя иногда он берёт ответственность за акции или выдвигает требования через нелегальные трансляции. Многие полагают, что Фронт тайно сотрудничает с Блюстителями, а кто-то из его членов даже заседает в Совете Соглашения.</p><p>Отношения ФОА с обществом осложняются ещё и тем, что у его членов нет общих убеждений, помимо цели препятствовать порабощению андроидов. Кто-то стремится выполнить задачи максимально мирным путём, другим плевать на чью-то смерть, одних заботит только настоящее рабство, в то время как другие хотят искоренить все ростки предрассудков в отношении искусственно созданных рас.</p><p>До настоящего времени самым противоречивым является радикальное меньшинство Перцептионистов. Большинство обитателей миров Соглашения сходится во мнении, что наличие души отличает людей от вещей, поскольку и андроиды, и разумные машины (например, анациты) обретают душу в момент пробуждения. Перцепционисты же утверждают, что даже если сразу не понятно, разумна ли машина или она просто следует программе, её нужно наделить всеми правами разумного существа. Эта отколовшаяся группа малочисленна, но настаивает на «освобождении» законно приобретённых роботов и корабельных ИИ и не гнушается прибегать к насилию, чем в последние годы наносит непоправимый вред репутации ФОА.</p>',8)])])}const f=t(n,[["render",o]]);export{m as __pageData,f as default};
