import{_ as e,c as r,o as t,ah as n}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"Барату","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Барату.md","filePath":"content/Расы/Расы Alien Archive/Барату.md"}'),i={name:"content/Расы/Расы Alien Archive/Барату.md"};function l(o,a,s,h,c,d){return t(),r("div",null,[...a[0]||(a[0]=[n('<h1 id="барату" tabindex="-1">Барату <a class="header-anchor" href="#барату" aria-label="Permalink to “Барату”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Вын, +2 Мдр, –2 Лов<br><strong>ПЗ:</strong> 6</p><p><strong>Размер и тип:</strong> молодые барату — аберрации среднего размера.<br><strong>Ночное зрение:</strong> молодые барату видят в темноте на расстоянии до 60 футов.<br><strong>Летун:</strong> базовая скорость молодых барату равна 0, но они получают полёт 30 футов (Экс, средняя манёвренность).<br><strong>Ограниченная телепатия:</strong> молодые барату могут мысленно общаться с любыми существами в пределах 30 футов, если знают с ними хотя бы один общий язык.<br><strong>Странная анатомия:</strong> молодые барату получают бонус +1 (раса) к испытаниям Стойкости.</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="начальная-адаптация-экс" tabindex="-1">Начальная адаптация (Экс) <a class="header-anchor" href="#начальная-адаптация-экс" aria-label="Permalink to “Начальная адаптация (Экс)”">​</a></h4><p>Тело молодого барату изменчиво и может приспособиться ко множеству различных ситуаций. В качестве быстрого действия молодой барату может один раз за каждые 1d4 раунда изменить своё тело и биохимию, чтобы получить одно из следующих свойств до начала своего следующего хода. В отличие от более взрослых барату, молодые особи редко способны на более сложные изменения.</p><ul><li>Изменения верхних конечностей позволяют барату наносить атаками в ближнем бою дополнительный урон, равный модификатору его Силы.</li><li>Плотный эпидермис даёт бонус +1 (раса) к КБ.</li><li>Развитые нижние конечности дают базовую скорость 15 футов.</li><li>Изменения на молекулярном уровне дают устойчивость 2 к одному типу энергии (звуку, кислоте, огню, холоду или электричеству).</li><li>Удлинённые конечности увеличивают зону досягаемости до 10 футов.</li></ul><h4 id="попутчик-экс" tabindex="-1">Попутчик (Экс) <a class="header-anchor" href="#попутчик-экс" aria-label="Permalink to “Попутчик (Экс)”">​</a></h4><p>Молодые барату недостаточно опытны, чтобы при слиянии чем-то помочь взрослым особям, но всё-таки могут физически слиться с ними для защиты, используя способность слияние взрослого барату. Находясь в слиянии, молодые барату добавляют получившемуся существу свои ПЗ, но не адаптации.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Барату — дирижаблеподобные существа, венец биологической эволюции Бреседы, газового гиганта. Они отдалённо напоминают медуз и обладают несколькими необычными эволюционными адаптациями. Прежде всего следует отметить их способность как инстинктивно, так и по собственному желанию переписывать свой генетический код, изменяя биохимию, что позволяет им создавать огромное количество различных веществ (и даже сложные биокомпланты) внутри тел, благодаря чему барату весьма удачно вписались в экономическую систему миров Соглашения. Это существо повлияло и на их понимание концепций богатства и торговли, но куда более важна их способность слияния с сородичами, в результате чего образуется более крупная сверхсущность, обладающая коллективным разумом. Объединённое существо — не просто совокупность входящих в него барату, а самостоятельное создание с уникальным и независимым мышлением, которое, в свою очередь, зачастую распадается на отдельных барату после решения определённой задачи или отражения угрозы.</p><p>Барату весьма уживчивы, но не находят взаимопонимания с некоторыми другими расами, так как из-за частых слияний понятие собственного «я» для барату достаточно туманно. Исключением из этого правила служат молодые барату, выросшие среди гуманоидов, — они гораздо лучше понимают образ мышления существ, живущих одиночной, обособленной жизнью. По сравнению со взрослыми особями молодые барату гораздо более независимы и предприимчивы, но из-за привычки к менталитету гуманоидов они с трудом достигают слияния с сородичами. Большинство молодых барату рано или поздно перерастает этот период, получая способность к полному единению, но в последние годы всё больше и больше барату целенаправленно задерживаются в молодом состоянии. Несмотря на то, что многие барату остаются самостоятельными существами большую часть жизни, на склоне лет они часто вливаются в огромные постоянные сущности, играющие роль корпораций, правительств или хранилищ знаний.</p>',14)])])}const b=e(i,[["render",l]]);export{p as __pageData,b as default};
