import{_ as t,c as e,o as n,ah as a}from"./chunks/framework.MgE-7MWY.js";const g=JSON.parse('{"title":"Вертани","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Вертани.md","filePath":"content/Расы/Расы Alien Archive/Вертани.md"}'),o={name:"content/Расы/Расы Alien Archive/Вертани.md"};function s(i,r,p,_,c,l){return n(),e("div",null,[...r[0]||(r[0]=[a('<h1 id="вертани" tabindex="-1">Вертани <a class="header-anchor" href="#вертани" aria-label="Permalink to “Вертани”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Вын, +2 Инт, –2 Сил<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> вертани — гуманоиды среднего размера с подтипом «вертани».<br><strong>Сумеречное зрение:</strong> вертани видят при низком уровне освещённости так же хорошо, как и при среднем.<br><strong>Мимикрирующая кожа:</strong> см. стр. 18.<br><strong>Привычная аугментация:</strong> вертани уже очень долго имплантируют различные устройства в свои тела. Вертани может установить дополнительную аугментацию (только киберимплант) в одну часть тела с уже установленной аугментацией.<br><strong>Эксперт:</strong> вертани обладают разными навыками, но каждый из них предпочитает фокусироваться на чём-то одном. Вертани получают дополнительную черту Эксперт.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Вертани, основные обитатели Верчеса, были в числе первых рас миров Соглашения, построивших космические корабли: без этого в суровых условиях находящейся в приливном захвате планеты выживать было непросто.</p><p>В среднем рост вертани составляет 8 футов, у них стройное телосложение и длинные конечности. Круглые глаза вертани полностью чёрные и выпуклые, как у мышей, и они способны по желанию менять цвет кожи, рисуя на ней замысловатые узоры. К пубертатному возрасту почти все вертани учатся управлять этой способностью, но на младенцах и детях проступают яркие и выразительные цветные узоры, показывающие их настроение. Некоторые взрослые намеренно не контролируют узоры, видя в хитросплетениях случайных линий пророческие предзнаменования.</p><p>Давным-давно общество вертани разделилось на три касты: Аугментированные, Чистые и Божьи Сосуды. Принадлежность к касте определяется в подростковом возрасте. Хотя немногие сейчас строго следуют системе, традиции всё же несут в себе толику культурной гордости и некоторые вертани подчёркивают свою принадлежность к касте.</p><p><strong>Аугментированные</strong> — вертани, модифицировавшие тело с помощью технологий (обычно киберимплантов). Среди первых пилотов эфириолётов (изящных судов, на которых вертани впервые отправились к звёздам) было много представителей этой касты. Аугментированные до сих пор славятся как одни из самых умелых пилотов миров Соглашения.</p><p><strong>Чистые</strong>, в противовес Аугментированным, полностью отказались от любых технологических аугментаций и были ответственны за производство пищи, управление и другие хозяйственные дела. У многих современных Чистых есть небольшие кибернетические и биотехнологические улучшения, но они по-прежнему гордятся традиционными ролями смотрителей.</p><p><strong>Божьи Сосуды</strong> верно служат живыми воплощениями божеств и демонстрируют это, огнём или кислотой выжигая на своей груди священные символы-посвящения. Божьи Сосуды никогда не скрывают клеймо, даже несмотря на то, что рубцовая ткань вокруг шрамов никогда полностью не заживает и больше не способна изменять цвет.</p><p>Культура вертани представляет собой пример свободного демократического сотрудничества. Вертани были вынуждены жить на узкой полосе, со всех сторон окружённой непригодными для обитания землями, и научились сотрудничеству без жестокости и порабощения. Они предпочитают не воевать за крохи ресурсов, а развивать технологии для их эффективного использования. Кольцо Наций Верчеса по-прежнему остаётся блестящим образцом мирового правительства, защищающего граждан, не ограничивая их свободу. Сама идея Соглашения была во многом вдохновлена этой частью культуры вертани.</p>',13)])])}const d=t(o,[["render",s]]);export{g as __pageData,d as default};
