import{_ as t,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Вичвирд","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Вичвирд.md","filePath":"content/Расы/Расы Alien Archive/Вичвирд.md"}'),o={name:"content/Расы/Расы Alien Archive/Вичвирд.md"};function s(p,r,i,_,c,l){return a(),e("div",null,[...r[0]||(r[0]=[n('<h1 id="вичвирд" tabindex="-1">Вичвирд <a class="header-anchor" href="#вичвирд" aria-label="Permalink to “Вичвирд”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Хар, +2 Инт, –2 Вын<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> вичвиды — гуманоидные чудовища среднего размера.<br><strong>Ночное зрение:</strong> вичвиды видят в темноте на расстоянии до 60 футов.<br><strong>Поглощение силы:</strong> см. стр. 22.<br><strong>Силовой снаряд:</strong> вичвиды получают следующее псевдозаклинание: 1 в день — волшебная стрела. За каждые две поглощённые посредством этой способности волшебные стрелы вичвирд может применить эту способность ещё один раз в день.<br><strong>Торговец:</strong> вичвиды повсеместно славятся как превосходные торговцы и посредники. Они получают бонус +2 (раса) к проверкам Блефа и Дипломатии.<br><strong>Четырёхрукой:</strong> у вичвидов четыре руки, каждой из которых они могут что-то держать или использовать. Несмотря на то, что многорукость позволяет иметь наготове больше предметов, количество совершаемых атак не увеличивается.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Вичвиды — увлечённые странники и опытные купцы, организующие торговые пути между планами и планетами.</p><p>У них четыре руки и серо-голубая кожа без волоссяного покрова, а глаза светятся, становясь ярче от впитываемой силы. Рост среднего вичвира составляет 7 футов, вес — приблизительно 300 футов. Они предпочитают свободные, струящиеся одеяния и характерные конические головные уборы и часто скрывают лица под масками или узорными шлемами. Если вичвирд оказывается на рынке впервые или желает остаться неузнанным во время важной сделки, он иногда закладывает пару гибких рук за спину и полностью скрывает лицо.</p><p>Несмотря на то, что вичвиды подпитываются поглощаемой ими магической силой, им всё равно необходимо дышать, пить, есть и спать, как большинству прочих существ, причём из еды они предпочитают чрезвычайно острые блюда. Средняя продолжительность жизни вичвирдов неизвестна, считается, что они живут веками, если не тысячелетиями.</p><p>Вичвиды — признанные прародители касат и акитонских шовадов. Неизвестно, почему они заселили Касат и Акитон разумными расами, созданными по своему образу и подобию, и сколько ещё неоткрытых планет они посетили с теми же целями. Известно, что именно благодаря вичвидам касаты создали корабль-мир «Идарии» и осуществили исход с родной планеты в миры Соглашения.</p><p>Вичвиды встречаются практически на всех планетах и планах, где есть цивилизованная торговля, предпочитая сухие регионы с тёплым климатом, но об их родном мире практически ничего не известно, кроме названия: Сайрун. Сведения о государственном устройстве и общественном укладе тоже отсутствуют, кроме распространённого мнения о том, что межпланетный и межплановый торговый вичвирд-ов управляет старейшины-олигархи.</p><p>Большинство вичвидов предпочитает торговать в одиночку, главным образом фокусируясь на одном секторе рынка (например, оружии или магических предметах), хотя многие торгуют и другими товарами. Практически все вичвиды настолько любят торговать, что порой кажется, что процесс обсуждения цены для них важнее самой сделки.</p><p>Обычно вичвиды путешествуют с командой верных наёмников-гуманоидов с когда-то посещённых миров. Наёмники никогда не обсуждают условия оплаты, возможно, контракт запрещает им об этом говорить.</p><p>Вичвиды могут встречаться где угодно в мирах Соглашения, но больше всего их на станции «Авессалом», Акитоне и «Идарии». Большинство вичвидов миров Соглашения состоит в межпланетной и межплановой торговой ассоциации «Тетрад».</p>',14)])])}const g=t(o,[["render",s]]);export{d as __pageData,g as default};
