import{_ as t,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Дроу","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Дроу.md","filePath":"content/Расы/Расы Alien Archive/Дроу.md"}'),o={name:"content/Расы/Расы Alien Archive/Дроу.md"};function s(p,r,i,l,c,_){return a(),e("div",null,[...r[0]||(r[0]=[n('<h1 id="дроу" tabindex="-1">Дроу <a class="header-anchor" href="#дроу" aria-label="Permalink to “Дроу”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лов, +2 Хар, –2 Вын<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> дроу — гуманоиды среднего размера с подтипом «эльф».<br><strong>Ночное зрение:</strong> дроу видят в темноте на расстоянии до 60 футов.</p><p><strong>Магия дроу:</strong> дроу получают следующие псевдозаклинания:</p><ul><li>Неограниченно — обнаружение магии, пляшущие огоньки.<br> УЗ для этих эффектов равен уровню персонажа. Кроме этого, при определении соответствия требованиям считается, что дроу обладают чертой Малая экстрасенсорная сила; если дроу берёт черту Экстрасенсорная сила, он может добавить сверхъестественную способность подсвечивающее сияние к списку доступных псевдозаклинаний.</li></ul><p><strong>Невосприимчивость дроу:</strong> дроу невосприимчивы к эффектам магического сна и получают бонус +2 (раса) к испытаниям против заклинаний и эффектов очарования.</p><p><strong>Острые чувства:</strong> дроу получают +2 (раса) к проверкам Внимания.</p><p><strong>Световая слепота:</strong> дроу ослеплен на 1 раунд, когда попадает под действие яркого света, и становится растворенным до тех пор, пока не покинет область яркого света.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Дроу белокуры, фиолетовожоки, красивы и абсолютно безжалостны. Обычные дроу составляют основу общества и вооружённых сил, а высокорожденные дроу правят ими. Общество дроу матриархально, поэтому идея мужчин незавиден, работа на один из благородных домов в качестве головореза — один из немногих вариантов, позволяющих вести пусть необязательно долгую, но более-менее комфортную жизнь.</p><p>В сражении дроу беспощадны и не брезгуют любой возможностью застать противника врасплох или заманить его туда, где у них будет преимущество. Кодекс чести и доблесть для дроу лишь пустой звук, а всё, что верит в подобные идеалы, — низшие существа, заслуживающие своей участи. Эти эльфы без каких-либо сожалений порабощают представителей других рас, заставляя их служить себе, используют их в качестве пушечного мяса при исследовании опасной местности и не брезгуют жертвовать ими, если это необходимо для отступления.</p><p>Экономика дроу основана преимущественно на добыче образцов различного оружия из недр планеты-корабля, которую они объявили своей, их изучении и последующей продаже. В мирах Соглашения дроу известны как поставщики лучшего и новейшего вооружения. Солдаты дроу специализируются на дистанционном оружии и обычно работают командами, чтобы прорвать оборону противника, но даже самый преданный снайпер без малейшего промедления воспользуется возможностью осуществить личную месть и в пылу сражения пристрелить соперника.</p><p>Некоторые дроу рождаются с особыми дарами и гораздо могущественнее обычных сородичей. Таких дроу называют высокорожденными. Они обычно появляются в семьях других высокорожденных дроу, принадлежащих к одному из правящих домов, благодаря использованию технологий генетической селекции, секреты которой тщательно охраняются. Тем не менее иногда высокорожденные дроу рождаются и в обычных семьях без какого-либо генетического вмешательства. Как только подобный дроу осознаёт свою исключительность, он пытается продвинуться по социальной лестнице, либо войдя в один из благородных домов, либо снискав личную славу. Благодаря своему могуществу высокорожденные дроу быстро растут в иерархии и получают признание за свои деяния. Не беда, если при этом общество недосчитается нескольких обычных дроу.</p><p>Наследница могущественного дома может стоять во главе одного из подразделений семейного предприятия по торговле оружием и путешествовать по мирам Соглашения и новым колониям в поисках покупателей, заинтересованных в приобретении новых технологий, которые её дом разрабатывает на основе реликвий, добытых из недр Апост. Некоторые наследницы укрепляют связь дома с демоническими покровителями и становятся могущественными мистиками, а другие обучаются искусству техномантов в надежде разгадать тайну портала в центре Врат Ночи («Основная книга правил», стр. 458–459).</p>',16)])])}const g=t(o,[["render",s]]);export{d as __pageData,g as default};
