import{_ as t,c as r,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Кало","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Кало.md","filePath":"content/Расы/Расы Alien Archive/Кало.md"}'),o={name:"content/Расы/Расы Alien Archive/Кало.md"};function s(p,e,i,c,_,l){return a(),r("div",null,[...e[0]||(e[0]=[n('<h1 id="кало" tabindex="-1">Кало <a class="header-anchor" href="#кало" aria-label="Permalink to “Кало”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лов, +2 Мдр, –2 Вын<br><strong>ПЗ:</strong> 2</p><p><strong>Размер и тип:</strong> кало — гуманоидные чудовища среднего размера с подтипом «подводный», поэтому могут дышать только в воде.</p><p><strong>Зрение кало:</strong> благодаря врождённым способностям к экологии и жизни в условиях недостаточного освещения кало получают слепозрение (звук) на расстоянии до 60 футов и сумеречное зрение.</p><p><strong>Незаметные пловцы:</strong> кало получают бонус +4 к проверкам Скрытности, пока находятся в воде.</p><p><strong>Перемещение кало:</strong> базовая скорость кало равна 20 футам, они получают скорость плавания 50 футов.</p><p><strong>Устойчивость к холоду:</strong> кало обитают в холодной воде и получают устойчивость к холоду 5.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Люди обычно сравнивают кало с летучими мышами из-за того, что те имеют тонкие перепонки между руками и ногами. На самом же деле эти крылоподобные образования являются плавниками, благодаря которым кало изящно и легко мчатся сквозь ледяные воды Кало-Махой, одной из лун Бреседы.</p><p>Когда кало имеет сине-зелёный оттенок и места прозрачна, а их выпуклые светящиеся глаза могут двигаться и фокусироваться независимо друг от друга.</p><p>Кало крайне цивилизованные и в целом мирные, поэтому они стали первыми из жителей лун, получившими независимость в рамках миров Соглашения, и теперь многие из их подводных городов, окружённых кораллами, оборудованы атмосферными отсеками для сухопутных сановников и экспатриантов, а на поверхности льда, покрывающего мировой океан, расположено множество торговых поселений. Кало призраками скользят сквозь тёмные океанские глубины и ориентируются в основном за счёт эхолокации, однако их города освещены множеством разноцветных огней, символизирующим художественные традиции этой расы.</p><p>У большинства кало есть хобби, связанное с наукой или изящными искусствами. Они не очень привязаны к традициям, но глубоко уважают собственную историю и называют детей и организации в честь событий и деятелей древности. Члены военных подразделений, к примеру, могут называться «охотниками на акул» несмотря на то, что они занимаются совсем другими вещами и название давно устарело. Некоторые кало по-прежнему следуют традициям, в частности, говорящие-с-глубиной могут общаться с обитателями глубин и даже управлять ими. В современном обществе мудрецы играют не такую важную роль, как в древности, однако к ним обращаются как к арбитрам или посредникам в случае возникновения споров общественного и государственно-го характера.</p><p>На суше кало не столь быстры, но их воины славятся спокойствием и точностью, особенно в условиях нулевой гравитации и под водой. Они используют криоружие против других рас, полагаясь на то, что естественные устойчивости защитят их от шальных выстрелов союзников.</p><p>Рост типичного кало 5,5 фута, а вес — 100 футов.</p>',16)])])}const g=t(o,[["render",s]]);export{d as __pageData,g as default};
