import{_ as r,c as t,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Космогоблин","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Космогоблин.md","filePath":"content/Расы/Расы Alien Archive/Космогоблин.md"}'),o={name:"content/Расы/Расы Alien Archive/Космогоблин.md"};function s(p,e,_,i,c,l){return a(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="космогоблин" tabindex="-1">Космогоблин <a class="header-anchor" href="#космогоблин" aria-label="Permalink to “Космогоблин”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +4 Лов, –2 Хар<br><strong>ПЗ:</strong> 2</p><p><strong>Размер и тип:</strong> космогоблины — гуманоиды небольшого размера с подтипом «гоблиноид».<br><strong>Ночное зрение:</strong> космогоблины видят в темноте на расстоянии до 60 футов.</p><p><strong>Быстрый:</strong> невзирая на размер, космогоблины очень быстры, их базовая скорость — 35 футов.</p><p><strong>Ломастер:</strong> см. стр. 46.</p><p><strong>Пронзья:</strong> космогоблины получают бонус +2 (раса) к проверкам Выживания, Инженерного дела и Скрытности.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>В гоблинских легендах говорится, что когда-то давным-давно племя очень смышлённых гоблинов пробралось на улетавший с Голарио-на звездолёт и попало на станцию «Авессалом». Оказавшись на космической станции, гоблины быстро проникли в самые захудалые её районы и поселились в инженерных тоннелях. Со временем гоблины научились делать оружие и броню из найденных запчастей, но результаты их экспериментов имеют обыкновение взрываться при первом удобном случае.</p><p>Космогоблины время от времени угнояли звездолёты и в результате расселились по галактике, но нигде их нет столько, сколько на «Авессаломе», к великому облегчению других миров, знакомых с ними не понаслышке.</p><p>Из-за крайней плодовитости этих существ с момента прибытия первых гоблинов на «Авессалом» сменилось уже множество поколений. Из-за гравитационных флуктуаций, совершенно нового рациона и периодических утечек радиации космогоблины существенно отличаются от гоблинов Голарио. Они чуть умнее и способны инстинктивно разбирать технологические устройства и собирать нечто соответствующее их странным представлениям о прекрасном. Кроме этого, они стали пошустрее и, ставну оставленный без присмотра лазерный пистолет или планшет, юрко скрываются в ближайшей вентиляционной шахте.</p><p>Некоторые космогоблины продолжают поклоняться древним гоблинским героям-богам, частично приспособив своё вероучение к текущему положению дел, но большинство почитает Триединого, бога машин. Из-за врождённой склонности к использованию технологий (без какого-либо понимания принципов их действия) они пришли к выводу, что у Триединого есть для них особое предназначение. Некоторые даже верят в то, что он приведёт их на райскую свалку, где они каждый день будут находить что-то новенькое, что можно разобрать на части. Пока что ни Триединый, ни его церковь официально не признают фанатичных космогоблинов последователями.</p><p>Голарионские гоблины славились прожорливостью, каждый день съедая столько же, сколько весят сами, притом в представлении гоблина едой могло быть вообще всё мало-малски съедобное. Космогоблинам на «Авессаломе» подобная роскошь могла только сниться, первое время они питались объедками и прочим мусором и в результате умерили аппетит, но по-прежнему тянут в рот всё, что не приколочено, — встретив незнакомый предмет, космогоблин с равной вероятностью попытается его либо съесть, либо разобрать. Если небольшой прибор сломался, то космогоблин вполне может искать поломку, засунув его в рот и хорошенько обмусолив.</p><p>Инстинктивная гоблинская ненависть и страх в отношении лошадей и собак также претерпели некоторые изменения. Теперь космогоблины называют любое неприятное им четвероногое существо (да и вообще что угодно похожей формы) «собакой» или «лошадью», в зависимости от размера. По этой же причине они называют своё грубое оружие ближнего боя собачьими тесаками; они схожи с охотничьими ножами, но некоторые предприимчивые племена научились делать микро- и ультрасеребраторные клинки. Ряд других племён предпочитает огненные винтовки и пистолеты, которые они называют «коженогами».</p><p>В анатомическом плане космогоблины несильно отличаются от голарионских сородичей (которых никто не видел уже несколько сотен лет). Космогоблины на несколько дюймов выше, а их красные глаза чуть темнее. Рост гоблина в среднем 3,5 фута, а вес — около 35 футов, причём основную его часть составляет голова.</p>',16)])])}const g=r(o,[["render",s]]);export{d as __pageData,g as default};
