import{_ as t,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Маракой","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Маракой.md","filePath":"content/Расы/Расы Alien Archive/Маракой.md"}'),o={name:"content/Расы/Расы Alien Archive/Маракой.md"};function s(p,r,i,_,c,l){return a(),e("div",null,[...r[0]||(r[0]=[n('<h1 id="маракои" tabindex="-1">Маракой <a class="header-anchor" href="#маракои" aria-label="Permalink to “Маракой”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Вын, +2 Мдр, –2 Лов<br><strong>ПЗ:</strong> 5</p><p><strong>Размер и тип:</strong> маракои — гуманоиды среднего размера с подтипом «маракой».<br><strong>Сумеречное зрение:</strong> маракои видят при низком уровне освещённости так же хорошо, как и при среднем.</p><p><strong>Древолаз:</strong> маракои получают скорость лазания 20 футов.</p><p><strong>Прирождённый охотник:</strong> маракои получают бонус +2 (раса) к проверкам Выживания.</p><p><strong>Слепое чутьё:</strong> маракои воспринимают звук кожи, а не ушами и получают слепое чутьё (звук) на расстоянии до 30 футов.</p><p><strong>Целкий хвост:</strong> хвост маракоя настолько ловкий, что ни в чём не уступает руке, поэтому им можно что-то держать или использовать. Это позволяет иметь наготове больше предметов, но количество совершаемых атак не увеличивается.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Маракои — коренные обитатели Мараты, одной из лун Бреседы. До недавних пор они считались примитивным народом, но благодаря межпланетной торговле быстро развивались технологически, хотя всё ещё придерживаются многих традиций предков. Маракои немного выше людей, а их тела покрыты шелковистой шерстью, которая, будто тысячи крохотных антенн, передаёт звуки их чувствительной коже. Обезьяньи хвосты маракоев настолько цепкие и ловкие, что с их помощью можно производить сложные манипуляции с предметами.</p><p>Из-за сложных родственных связей и множества полов маракои существенно отличаются от прочих рас. Большая часть гуманоидов, населяющих миры Соглашения, двупола, в то время как у маракоев целых семь разных полов, каждый из которых играет отдельную роль в репродуктивном процессе. Иша, куша и суша (что можно перевести примерно как «земной отец», «небесный отец» и «водный отец» соответственно) передают свою часть генетического материала уйше («делителю»). Вскоре после этого уйша передаёт эмбрион маракоя клише («носителю»), вынашивающему плод до конца срока. Новорождённый детёныш передаётся меше («колыбели») — сумчатому маракою, который носит его в сумке и нянчит всё время до окончания вскармливания. Самый необычный пол — зиша («посредник»). Зиши не принимают непосредственного участия в процессе размножения, но их присутствие необходимо, поскольку они каким-то образом также передают растущему маракою элементы своего генетического кода. Моногамные браки и тому подобные традиции маракоям неведомы, и несмотря на поток информации с других миров, большая часть считает подобные идеи странными или забавными.</p><p>В культуре маракоев с глубочайшим уважением относятся к жизни и институту семьи. Потеря нескольких соплеменников может лишить всё племя возможности размножаться, поэтому каждую жизнь необходимо ценить и защищать. Правило остаётся неизменным и для других форм жизни, за каждой охотой следует ритуальное чествование поверженной жертвы. Практически все маракои считаются членами одной большой семьи, поэтому межплеменные разногласия редко заканчиваются смертью. Маракои последних поколений начали работать на других мирах в качестве наёмников, и это внесло существенный разлад в их общество: кто-то убеждён, что в этом нет ничего плохого (по крайней мере до тех пор, пока маракои не убивают друг друга), а другие считают работу по найму неприемлемой для благородных стражей. Тем не менее даже маракои-наёмники не забывают о традиционном понимании чести и проводят ритуалы, чтобы почтить павших друзей и врагов.</p><p>Многие маракои бережно хранят древние обычаи охотников и собирателей, и некоторые племена всё ещё обитают в пещерах на склонах скал или бревенчатых общих домах глубоко в лесных зарослях, довольствуясь лишь самыми базовыми технологическими удобствами. Другие всё ещё пасут стада ящеров на каменистых равнинах, но всё же используют современное оружие и транспортные средства. Некоторая часть маракоев стремится к индустриализации, и за последние годы им удалось построить впечатляющие городские поселения, где они добывают и продают на экспорт природные ресурсы планеты, нарушая тем самым традиционный, первобытно-общинный подход к собственности. Между различными группами нарастает напряжение, и многие маракои опасаются непоправимого раскола общества.</p>',14)])])}const g=t(o,[["render",s]]);export{d as __pageData,g as default};
