import{_ as a,c as r,o as t,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Саркезианин","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Саркезианин.md","filePath":"content/Расы/Расы Alien Archive/Саркезианин.md"}'),o={name:"content/Расы/Расы Alien Archive/Саркезианин.md"};function s(_,e,i,c,p,l){return t(),r("div",null,[...e[0]||(e[0]=[n('<h1 id="саркезианин" tabindex="-1">Саркезианин <a class="header-anchor" href="#саркезианин" aria-label="Permalink to “Саркезианин”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лвк, +2 Хар, –2 Сил<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> саркезиане — гуманоиды крупного размера с подтипом «саркезианин».<br><strong>Сумеречное зрение:</strong> саркезиане видят при низком уровне освещённости так же хорошо, как и при среднем.</p><p><strong>Полёт в вакууме:</strong> саркезиане могут не дышать в течение 1 часа и не подвержены негативному воздействию вакуума. Потратив 1 ПР, саркезиане могут увеличить длительность этого эффекта на 1 час за 1 КО, а потратив 2 ПР — 2 часа за 1 КО. В вакууме саркезиане автоматически выпускают энергетические крылья, получая скорость полёта 60 футов (Св, средняя манёвренность). Крылья позволяют летать только в вакууме.</p><p><strong>Умелый:</strong> саркезиане получают один дополнительный пункт навыков на первом и каждом последующем уровне.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Саркезиане, решившие покинуть Диаспору, иногда становятся наёмниками — снайперами и разведчиками, поскольку из-за образа жизни привыкли иметь дело с далёкими целями. Они оттачивают врождённое терпение, чтобы дожидаться цели по несколько дней, устроив засаду на краю обрыва, в полуразрушенном здании или даже в космосе недалеко от места стыковки. За такую самоотверженность готовы хорошо платить, поэтому саркезиане-снайперы особенно востребованы в некоторых частях галактики.</p><p>Эти гуманоиды, предположительно, происходят от выходцев с исчезнувших двойных планет, уничтожение которых привело к возникновению пояса астероидов, именуемого Диаспорой. Саркезиане приспособились к жизни в условиях пониженной гравитации и разреженной атмосферы, ростом они от 10 до 15 футов, у них выпуклые глаза, тонкие и длинные конечности. Эти существа способны выживать в космосе, задерживая дыхание и выпуская сотканные из чистой энергии крылья, которые улавливают солнечный ветер и позволяют саркезианам парить между немногими обитаемыми астероидами и космическими платформами Диаспоры.</p><p>Благодаря магическим механизмам, созданным их предками, саркезиане уже долгое время поддерживают миры-колыбели — астероиды с магической атмосферой, гравитацией и теплом, где они могут спокойно жить и растить потомство. В сравнении с некоторыми планетами системы Голариона, миры-колыбели саркезиан — настоящая идиллия. На них есть место и полям, и лесам, и холмам, и озёрам, и маленьким городкам, в которых живёт лишь по несколько тысяч жителей. Многие из этих заповедных миров соединены между собой Межречьем, необычным водным путём, протекающим между астероидами и удерживаемым цилиндрическим силовым полем неведомого происхождения, которое не даёт воде утечь в космос.</p>',11)])])}const g=a(o,[["render",s]]);export{d as __pageData,g as default};
