import{_ as e,c as r,o as t,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Серый Пришелец","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Серый Пришелец.md","filePath":"content/Расы/Расы Alien Archive/Серый Пришелец.md"}'),o={name:"content/Расы/Расы Alien Archive/Серый Пришелец.md"};function i(s,a,l,h,p,_){return t(),r("div",null,[...a[0]||(a[0]=[n('<h1 id="серыи-пришелец" tabindex="-1">Серый Пришелец <a class="header-anchor" href="#серыи-пришелец" aria-label="Permalink to “Серый Пришелец”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +4 Инт, –2 Сил<br><strong>ПЗ:</strong> 2</p><p><strong>Размер и тип:</strong> серые пришельцы — гуманоиды небольшого размера с подтипом «серый пришелец».<br><strong>Ночное зрение:</strong> серые пришельцы видят в темноте на расстоянии до 60 футов.</p><p><strong>Магия серых:</strong> серые пришельцы получают следующие псевдозаклинания:</p><ul><li>1 в день — удар по разуму (1 круг);</li><li>Неограниченно — ступор, телепатическое сообщение.<br> УЗ для этих эффектов равен уровню персонажа.</li></ul><p><strong>Ограниченная телепатия:</strong> серые пришельцы могут мысленно общаться с любыми существами в пределах 30 футов, если знают с ними хотя бы один общий язык.</p><p><strong>Фазовое смещение:</strong> в качестве ответного действия серый пришелец может сместиться из реальности — одна атака по нему получает штраф –2 (для большинства подобных существ). Количество использований этой способности в день равно модификатору Выносливости серого пришельца.</p><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="зондирование-св" tabindex="-1">Зондирование (Св) <a class="header-anchor" href="#зондирование-св" aria-label="Permalink to “Зондирование (Св)”">​</a></h3><p>Прикоснувшись к существу, серый пришелец устанавливает с ним мощную телепатическую связь, обмениваясь с ним информацией и ощущениями с ужасающей быстротой. Существо, к которому прикоснулся серый пришелец (атака в ближнем бою против ККБ), проходит испытание Воли со СЛ 15 и при провале ошеломлено на 1d4 раунда из-за перегрузки органов чувств. Если разумное существо, к которому прикоснулся серый пришелец, находится в сознании и парализовано, то он вместо этого может в качестве основного действия произвести зондирование его разум в поисках ответа на простой вопрос (например, «Откуда прибыл ваш звездолёт?») или информацию касательно одной из известных существо общих тем. Цель может сопротивляться зондированию, успешно пройдя испытание Воли со СЛ 15. Зондировать каждое существо можно не чаще раза в минуту, но если серый пришелец в течение 1 минуты будет непрерывно касаться его, то может выбрать один из зависящих от Интеллекта навыков этого существа, в который вложен хотя бы 1 пункт. В течение следующих 24 часов серый пришелец при совершении проверок этого навыка может использовать модификатор навыка вместо своего. Серый пришелец не может использовать оба варианта способности одновременно, поскольку перегрузка органов чувств цели мешает поискам информации.</p><h3 id="сонныи-паралич-св" tabindex="-1">Сонный паралич (Св) <a class="header-anchor" href="#сонныи-паралич-св" aria-label="Permalink to “Сонный паралич (Св)”">​</a></h3><p>В качестве основного действия серый пришелец может парализовать спящее существо в пределах 30 футов, которое он видит. Если цель успешно пройдёт испытание Воли со СЛ 15, она остаётся спящей и становится невосприимчивой к сонному параличу этого серого пришельца на 24 часа. При провале цель просыпается, но парализована на 1d6 минут. Любая атака или иное враждебное действие (за исключением способности зондирование для поиска информации) немедленно прекращает паралич. Если паралич не прерван и его длительность закончилась, цель засыпает и забывает о произошедшем, как если бы её воспоминания были стёрты манипуляцией памятью. Жертва может пройти испытание Воли со СЛ 15, чтобы сохранить воспоминания. При успехе она смутно помнит о параличе и зондировании.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Фазовое смещение (Св): серые пришельцы слегка не совпадают по фазе с Материальным планом. Они могут свободно проходить сквозь стены и материальные предметы (но не небестелесных существ), если начинают и заканчивают свой ход за пределами препятствия. Кроме этого, атаки и эффекты, напрямую нацеленные на этих существ, промахиваются с вероятностью 20%, а эффекты, воздействующие на область, наносят им только половину урона. Эффекты силы работают против серых пришельцев как обычно.</p><p>Никто не знает, на какой планете и даже в какой галактике обитают серые пришельцы, но пугающие сообщения о похищениях, ужасном параличе и загадочных экспериментах поступают с бесчисленных миров с момента начала освоения космоса. Полученная информация неясна и ненадёжна — жертвы сохраняли лишь смутные воспоминания о том, что они подвергались различным исследованиям в ярко освещённых операционных или просыпались в замкнутом тёмном пространстве. Цели и методы похитителей остаются неясны. Похищенные же, вне зависимости от расы и обстоятельств похищения, рассказывают о похитителях следующее: их присутствие ощущается как нечто чужеродное, а подопытных они унижают и ни во что не ставят их свободу воли и достоинство.</p><p>Серые пришельцы (или просто «серые») общаются исключительно при помощи телепатии, даже между собой. Их лица и блестящие чёрные глаза показывают лишь тень эмоций, а движения грациозны, но любому действию или шагу предшествует несколько моментов промедления, будто серый обдумывает его наперёд, потому большинство рас считают этих гуманоидов непонятными и неприятными.</p><p>О мотивах подобного поведения серых пришельцев известно немного, и на сегодняшний день ни одна из попыток наладить с ними дипломатический контакт не увенчалась успехом, но похоже, что основная цель этих существ — поиск информации, а не завоевания. Похищенные жертвы практически всегда возвращаются в целости и сохранности, но внезапное появление шрамов или имплантов вызывает массу вопросов, несмотря на способность серых пришельцев стирать похищенным память. Исследователи пытаются установить конечные цели, ради которых идёт сбор данных, и узнать, для чего информация нужна в настоящий момент времени. Кто-то из учёных полагает, что серые пришельцы планируют полномасштабное вторжение, другие же считают, что таким странным и пугающим способом эти чуждые нам существа просто удовлетворяют своё любопытство.</p><p>Раньше сообщения о похищениях списывали на теории заговоров и психические заболевания, но после начала использования Потока встречи с серыми пришельцами резко участились. Их летающие тарелки выжигают в тёмных уголках Потока и внезапно появляются из ниоткуда, нападая на зазевавшихся или невнимательных путешественников. Звездолёты серых пришельцев, как и их создатели, приспособлены не для боя, а для уклонения и обезвреживания целей и используют лучи захвата и электромагнитное оружие, чтобы вывести корабль противника из строя, сохранив экипаж для проведения экспериментов. Пленники вскоре оказываются неподвижно лежащими под яркими светильниками, а захватчики превращаются в размытые формы, когда мысли и воспоминания смазываются под воздействием способностей серых пришельцев.</p>',20)])])}const b=e(o,[["render",i]]);export{d as __pageData,b as default};
