import{_ as a,c as e,o as t,ah as n}from"./chunks/framework.MgE-7MWY.js";const _=JSON.parse('{"title":"Урог","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Урог.md","filePath":"content/Расы/Расы Alien Archive/Урог.md"}'),o={name:"content/Расы/Расы Alien Archive/Урог.md"};function s(i,r,l,h,c,p){return t(),e("div",null,[...r[0]||(r[0]=[n('<h1 id="урог" tabindex="-1">Урог <a class="header-anchor" href="#урог" aria-label="Permalink to “Урог”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Вын, +2 Инт, –2 Хар<br><strong>ПЗ:</strong> 6</p><p><strong>Размер и тип:</strong> уроги — волшебные звери крупного размера; занимаемое ими пространство равно 10 футам, а зона досягаемости — 5 футам.<br><strong>Ночное зрение:</strong> уроги видят в темноте на расстоянии до 60 футов.<br><strong>Сумеречное зрение:</strong> уроги видят при низком уровне освещённости так же хорошо, как и при среднем.<br><strong>Бестактный:</strong> уроги руководствуются прежде всего фактами, ценят открытость и прямоту, не сильно заботясь о чувствах окружающих. Уроги получают штраф –2 к проверкам Блефа и Дипломатии.<br><strong>Медлительный:</strong> базовая скорость урогов — 20 футов.<br><strong>Ограниченная телепатия:</strong> уроги могут мысленно общаться с любыми существами в пределах 30 футов, если знают с ними хотя бы один общий язык.<br><strong>Умелый:</strong> уроги получают один дополнительный пункт навыков на первом и каждом последующем уровне.<br><strong>Устойчивость к электричеству:</strong> уроги получают устойчивость к электричеству 5, которая суммируется с устойчивостью к электричеству от одного другого источника.<br><strong>Электролокация:</strong> Когда урог соприкасается с кристаллической или металлической поверхностью, он может определить присутствие других существ в пределах 60 футов, если они тоже соприкасаются с этой поверхностью (невзирая на стены и прочие препятствия). В остальном способность аналогична слепому чутью (зрение).</p><blockquote><p>Попозже подумать что с этим делать</p></blockquote><h3 id="полупроводник-экс" tabindex="-1">Полупроводник (Экс) <a class="header-anchor" href="#полупроводник-экс" aria-label="Permalink to “Полупроводник (Экс)”">​</a></h3><p>Урог может изменять состав своего кремневого тела, увеличивая или уменьшая его электропроводность. В качестве сопутствующего действия урог может потратить 1 Р, чтобы на 1 раунд лишиться своей естественной устойчивости к электричеству и получить бонус к урону от электрического разряда, равный модификатору своей Выносливости. Эффект действует до начала следующего хода урога.</p><h3 id="электрическии-разряд-экс" tabindex="-1">Электрический разряд (Экс) <a class="header-anchor" href="#электрическии-разряд-экс" aria-label="Permalink to “Электрический разряд (Экс)”">​</a></h3><p>В качестве дистанционной атаки урог может выпустить в одного противника небольшой электрический разряд (шаг дистанции 40 футов).</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Дикон, один из многочисленных спутников Бреседы, широко известен своей полностью кремниевой экосистемой. В этом мире достаточно много углеродных форм жизни, прибывших с других миров Соглашения для добычи ценных кристаллов и охоты на кремниевых хищников ради естественных сверхпроводников, но в кристаллических лесах и на многогранных плато живут и коренные разумные обитатели, например уроги. Анатомия этих существ весьма необычна. В повседневной, «походной» форме урог выглядит как кристаллический слизень или улитка: его переливающиеся панцирные чешуйки свисают почти до земли, скрывая конечности, а сам урог парит над поверхностью земли на тонкой электромагнитной подушке, создаваемой микроскопическими жгутиками. В «активной» же форме, которую они принимают при спаривании, в сражении и для взаимодействия с другими расами или непосредственным окружением, они встают на дыбы и расправляют многочисленные конечности. На длинной шее урога расположена голова, украшенная мощным клювом, который используется исключительно для сражений и брачных игр. Едят же уроги своим брюшком: расположенные там жгутики точечными электромагнитными импульсами постепенно разрушают молекулярные связи и отделяют крошечные частицы поглощаемой пищи. Эти «укусы» настолько незаметны, что другим расам даже понятно, что происходит, когда предмет буквально тает на глазах без видимых на то причин. Это позволяет урогам поглощать любую пищу, отделяя и усваивая только нужные им молекулы и не трогая всё остальное, но они всё-таки предпочитают кремниевые растения родной планеты. Благодаря этому необычному способу питания уроги практически полностью невосприимчивы к ядам: их тело просто отторгает все ненужные молекулы.</p><p>Вариантов питания у урогов много, а естественных врагов благодаря внушительному размерам и крепкому панцирю мало, поэтому большую часть времени они посвящают размышлениям и созерцанию. Они постоянно черпают вдохновение из сложных геометрических форм своего кристаллического мира и, как кажется, бесцельно прогуливаются по поверхности луны, как поодиночке, так и небольшими группами, работая над математическими задачами или проводя загадочные эксперименты. За медлительностью движений скрыт невероятно острый разум: уроги настолько глубоко задумываются над запутанными математическими и научными загадками, что даже не сразу реагируют на внешние раздражители.</p><p>Уроги зачастую всецело отдаются поискам наиболее эффективного решения какой-либо задачи и могут потратить огромное количество времени, чтобы в конечном итоге сократить время выполнения процесса на несколько секунд. Уроги славятся точностью и педантизмом и поэтому становятся превосходными астронавигаторами, инженерами, учёными и консультантами в мирах Соглашения, хотя большинство их инопланетных коллег признаёт, что взаимоотношения с ними могут быть, мягко говоря, непростыми. В общем это дружелюбные существа, по крайней мере в их собственном представлении, но общественные приличия они соблюдать не хотят. Заскучавший урог запросто может перестать обращать внимание на коллегу или просто развернуться и уйти, вне зависимости от влятельности собеседника и важности обсуждаемой темы. Тем, кто заслужил внимание урога, всё равно придётся мириться с его бесцеремонностью. Эмоции этих существ достаточно сложны для понимания и основываются в основном на удовлетворении от решения задачи, разочаровании при неудаче или предвкушении многообещающего исследования, поэтому уроги охотно указывают на ошибки — неважно, свои или чужие. Делают они это, само собой, на благо общего дела и ради достижения лучших результатов, но немногим из людей хватает терпения и самообладания, чтобы принимать нескончаемый поток критики со стороны увлечённого процессом урога. Иногда уроги выбирают стезю искателей приключений, потому что убеждены, что математические и научные секреты спрятаны в далёких уголках галактики, которые с их маленькой луны не разглядеть, а кроме этого — потому что не доверяют научным данным, собранным другими видами. Особые способности урогов иногда ослабевают, если они долго не были на Диконе; скорее всего, это происходит из-за изменившегося рациона и отсутствия определённых электрических полей, создаваемых самой луной. Самые успешные уроги-космоплаватели в конечном итоге начинают видеть смысл в помощи своим менее вдумчивым компаньонам и становятся превосходными бортинженерами и офицерами по науке, если на звездолёте достаточно места для их крупных тел и огромного эго.</p>',14)])])}const g=a(o,[["render",s]]);export{_ as __pageData,g as default};
