import{_ as t,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Шовад","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы Alien Archive/Шовад.md","filePath":"content/Расы/Расы Alien Archive/Шовад.md"}'),o={name:"content/Расы/Расы Alien Archive/Шовад.md"};function s(i,r,_,c,p,l){return a(),e("div",null,[...r[0]||(r[0]=[n('<h1 id="шовад" tabindex="-1">Шовад <a class="header-anchor" href="#шовад" aria-label="Permalink to “Шовад”">​</a></h1><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +4 Сил, –2 Инт<br><strong>ПЗ:</strong> 6</p><p><strong>Размер и тип:</strong> шовады — гуманоидные чудовища крупного размера, занимаемое ими пространство и зона досягаемости равны 10 футам.<br><strong>Ночное зрение:</strong> шовады видят в темноте на расстоянии до 60 футов.<br><strong>Быстрый:</strong> базовая скорость шовадов — 40 футов.<br><strong>Морозостойкий:</strong> шовады получают устойчивость к холоду 5, которая суммируется с устойчивостью к холоду от одного другого источника.<br><strong>Четырёхрукий:</strong> у шовадов четыре руки, каждой из которых они могут что-то держать или использовать. Несмотря на то, что многорукость позволяет иметь наготове больше предметов, количество совершаемых атак не увеличивается.<br><strong>Шовадская свирепость:</strong> раз в день, если ПЗ шовада опустился до 0, но шовад не умер, он может продолжить сражаться ещё один раунд. Его ПЗ опускается до 0, он оказывается при смерти (следуя обычным правилам), но может действовать как обычно до конца своего следующего хода, после чего немедленно теряет сознание. Если до этого шовад получит дополнительный урон, то перестаёт совершать действия и также теряет сознание.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>«Шовад-нех», или просто шовады, — четверорукие гиганты с Акитона, уже тысячелетия ведущие кочевой образ жизни. Шовады весьма самодостаточны и способны сами изготавливать оружие. Они всегда старались избегать больших городов и представителей других рас, делая исключения только ради торговли, поэтому, когда на Акитоне случился экономический кризис, шовады особенно не пострадали и впоследствии стали предлагать услуги проводников, охотников и наёмников, чаще всего в обмен на более совершенное оружие и броню. На сегодняшний день шовады крайне не востребованы из-за боевых умений, непревозмождённого знания пустошей Акитона и нерушимой верности кодексу чести. Воины могут действовать как в одиночку, так и в составе традиционных кланов или в современных отрядах наёмников, объединяющих древние традиции с требованиями современного мира. Шовады стали чаще встречаться в новых мегаполисах Акитона, но в большинстве своём всё же предпочитают жить среди холмов и каньонов планеты.</p><p>Шовады носят доспехи и традиционные верёвочные или кожаные портупеи, практически не снимая. Жизнь среди шовад-не сурова, молодые шовады начинают обучаться боевым искусствам ещё до достижения подросткового возраста. К 20 годам шовад считается взрослым, способным защищать и себя, и свой клан даже от самых опытных воинов. Рост шовадского воина достигает 12 футов, вес — 500 фунтов, а живут они до 90–120 лет, если не погибнут в сражениях.</p><p>Большинство кланов добывают припасы и пропитание охотой, собирательством и в ходе нападений на соседние кланы и конвои корпораций. Шовады верят, что открытая агрессия — не порок, а неотъемлемая часть жизни и что каждый должен быть сильным и уметь за себя постоять. Однако на первом месте у них стоит честь, и нарушение клятья или закона клана влечёт за собой участие хуже смерти. Законы горожан, напротив, не значат для них ничего, если, конечно, не было дано обещание им подчиняться.</p><p>Шовады крайне редко строят постоянные поселения. Клан вполне может пройти по пустыням не одну тысячу миль и делить территорию с другими кланами или видами, при этом почти не конфликтуя.</p><p>Большинство кланов остаётся на Акитоне, но для опытного бойца всюду найдётся работа по найму, поэтому некоторые шовадские кланы, менее привязанные к традициям, теперь путешествуют на звездолётах. Их часто нанимают различные корпорации и объединения, например общество Звёздных искателей. Некоторые шовады неожиданно обнаруживают в себе неутолимую страсть к путешествиям и, отправившись работать на другую планету, редко возвращаются на Акитон.</p>',11)])])}const g=t(o,[["render",s]]);export{d as __pageData,g as default};
