import{_ as t,c as e,o as r,ah as n}from"./chunks/framework.MgE-7MWY.js";const h=JSON.parse('{"title":"Полурослики","description":"","frontmatter":{},"headers":[],"relativePath":"content/Расы/Расы CRB/Полурослики.md","filePath":"content/Расы/Расы CRB/Полурослики.md"}'),o={name:"content/Расы/Расы CRB/Полурослики.md"};function s(_,a,p,i,c,l){return r(),e("div",null,[...a[0]||(a[0]=[n('<h1 id="полурослики" tabindex="-1">Полурослики <a class="header-anchor" href="#полурослики" aria-label="Permalink to “Полурослики”">​</a></h1><h2 id="расовые-особенности" tabindex="-1">РАСОВЫЕ ОСОБЕННОСТИ <a class="header-anchor" href="#расовые-особенности" aria-label="Permalink to “РАСОВЫЕ ОСОБЕННОСТИ”">​</a></h2><p>Изменения характеристик: +2 Лвк, +2 Хар, –2 Сил<br> ПЗ: 2</p><p><strong>Размер и тип</strong>: полурослики — гуманоиды небольшого размера с подтипом «полурослик».</p><p><strong>Острые чувства</strong>: полурослики получают бонус +2 (раса) к проверкам Внимания.</p><p><strong>Проворный</strong>: полурослики получают бонус +2 (раса) к проверкам Акробатики и Атлетики.</p><p><strong>Проницливость</strong>: полурослики получают бонус +2 (раса) к проверкам Скрытности. Кроме того, полурослики уменьшают штраф к проверке Скрытности во время перемещения на 5, а при выстреле из засады снижают штраф к проверке Скрытности на 10.</p><p><strong>Удача полурослика</strong>: полурослики получают бонус +1 (раса) ко всем испытаниям, а для испытаний против эффектов с дескриптором «ужас» этот бонус увеличивается до +3.</p><hr><h2 id="полурослики-1" tabindex="-1">ПОЛУРОСЛИКИ <a class="header-anchor" href="#полурослики-1" aria-label="Permalink to “ПОЛУРОСЛИКИ”">​</a></h2><p>Полурослики в мирах Соглашения и их колониях почти столько же, сколько и людей. Известное предание гласит, что полурослики были среди первых гуманоидов, покинувших пределы Голариона и освоивших поселения среди звёзд. Когда-то полурослики считали бездомными скитальцами, но с тех пор они усели космос колониями, а полуросличьи флоты-караваны из десятков звездолётов всевозможных моделей и возрастов курсируют по торговым путям в поисках новых достопримечательностей, историй, сплетен и диковинных иноземных товаров. Хрупкое телосложение полуросликов и склонность к странствиям делают их излюбленной мишенью бандитов и разбойников, но эта стойкая раса снова и снова восстанавливалась после тяжёлых испытаний благодаря взаимовыручке, оптимизму и, пожалуй, отличительным чертам своей расы — проворству и проницливости.</p><p>Полурослики одарены молниеносными рефлексами, очаровательной уверенностью в себе и очевидным бесстрашием, благодаря чему славятся по всей галактике как атлеты, звёзды и исследователи. Разумеется, такая потрясающая репутация в глазах общественности едва ли относится ко всей расе. Почти все полурослики переживают период безудержной смелости в молодости, но быстро перерастают это чувство и становятся счастливыми и любящими домоседами, чьи дни наполнены тяжёлой работой, а вечера — встречами в кругу друзей. Подобно людям, полурослики могут похвастаться большим разнообразием цветов кожи, волос и глаз, но у них сравнительно маленькие тела, большие стопы и кисти рук. Они легко заводят друзей повсюду, предпочитая переговоры или обмен насмешками вооружённым столкновениям. Несмотря на общительную натуру, у полуросликов мало постоянных союзников, их история наполнена долгими годами унижения и насилия, что заставляет их проявлять осмотрительность в общении, чтобы снова не оказаться в трудной ситуации. У них много общего с исками, особенно любовь к путешествиям и торговле, но полурослики не обладают такими техническими наклонностями. Обе расы часто соперничают за рынки сбыта, права на добычу и торговые пути. С кем полурослики действительно ладят, так это с людьми, чья история тесно переплетается с их собственной, к добру или к худу.</p><p>Иногда хладнокровие полуросликов, особенно перед лицом страшной опасности, обескураживает представителей других рас. Некоторые находят весёлый нрав и решительность полуросликов раздражающими, но часто меняют мнение, когда становится ясно, что их смелость спасает положение. Полурослики практически никогда не обижаются на подобное отношение, считая его естественным проявлением неуверенности других рас в собственных силах.</p>',13)])])}const m=t(o,[["render",s]]);export{h as __pageData,m as default};
